mapper.insert:
    	
			SELECT AUTONUM.NEXTVAL FROM DUAL
		
		INSERT INTO BOARD (
						boardNo
					    ,title
					    ,content
					    ,memId
					    ,regDate
					    ,boardDelYn
					    
					) VALUES (
						#{boardNo}
					    ,#{title}
					    ,#{content}
					    ,#{memId}
					    ,SYSDATE
					    ,'n'
					)

mapper.select:
SELECT * FROM BOARD
WHERE boardNo = #{boardNo}
	AND boardDelYn = 'n'

mapper.update:
   	UPDATE BOARD
	SET
		title = #{title}
		,content = #{content}
    
	WHERE
		boardNo = #{boardNo}


	UPDATE CMNT
	SET
	    cmntContent = #{cmntContent}
	WHERE
	        cmntNo = #{cmntNo} AND
	        boardNo = #{boardNo}

UPDATE CMNT
SET
    cmntContent = #{cmntContent}
WHERE
        cmntNo = #{cmntNo} AND
        boardNo = #{boardNo}
